import { Button, SafeAreaView, ScrollView, View } from "react-native-web";
import { CommonClass } from "../styles/Commonclass";
import { dataview } from "../styles/Dataview";
import { JPG_CONTENT_TYPE, PNG_CONTENT_TYPE } from "../helper/extrapropertise";
import { Card, Text } from "react-native-paper";
import { BodyList } from "./BodyList";

const ResponsiveCard = ({ item }) => {
    return (
        <SafeAreaView >
            {
                (item.ContentType === PNG_CONTENT_TYPE || item.ContentType === JPG_CONTENT_TYPE)

            }
            <Card>
                <Card.Content>
                    <Text variant="bodyMedium">{item.fileName || "Show File"}</Text>
                    {
                        (item.ContentType === PNG_CONTENT_TYPE || item.ContentType === JPG_CONTENT_TYPE)
                            ?
                            <img style={dataview.img} src={item?.value} alt="link chalana"></img>
                            : <Button
                                title={item.fileName || "Show File"}
                                onPress={async () => {
                                    const r = await fetch(item.value);
                                    const blob = await r.blob();
                                    window.open(URL.createObjectURL(blob), "_blank");
                                }} />
                    }
                </Card.Content>
            </Card>
        </SafeAreaView>
    )
}

export const ShowTableFiles = ({ route, navigation }) => {
    const { key, value } = route?.params;
    return (
        <BodyList
            body={
                <ScrollView >
                    <View style={dataview.heightAndOverflow}>
                        <table style={CommonClass.table}>
                            {
                                value.map((item) => <ResponsiveCard key={key} item={item} />)
                            }
                        </table>
                    </View>
                </ScrollView>
            }
            label={key}
        />
    )
}
