//                         const r = await fetch(item.value);
//                         const blob = await r.blob();
//                         window.open(URL.createObjectURL(blob), "_blank");
//                     }} />
//         }
//     </Card.Content>
// </Card>
const ResponsiveCard = ({ item }) => {
    return (
        <SafeAreaView >
            {
                (item.ContentType === PNG_CONTENT_TYPE || item.ContentType === JPG_CONTENT_TYPE)
                    ? <FileCard
                        item={item}
                    />
                    : <Button
                        title={item.fileName || "Show File"}
                        onPress={async () => {
                            const r = await fetch(item.value);
                            const blob = await r.blob();
                            window.open(URL.createObjectURL(blob), "_blank");
                        }} />
            }
        </SafeAreaView>
    )
}

export const ShowTableFiles = ({ route, navigation }) => {
    const { key, value } = route?.params;
    return (
        <BodyList
            body={
                <ScrollView >
                    <View style={dataview.heightAndOverflow}>
                        <table style={CommonClass.table}>
                            {
                                value.map((item) => <ResponsiveCard key={key} item={item} />)
                            }
                        </table>
                    </View>
                </ScrollView>
            }
            label={key}
        />
    )
}
