export const CommonClass = {
    textStyle: {
        flex: 1,
        color: '#000',
        fontSize: 16,
        paddingHorizontal: 10,
        paddingVertical: 5,
        borderRadius: 8,
        shadowColor: '#ccc',
        shadowOffset: { width: 0, height: 3 },
        shadowOpacity: 0.2,
        shadowRadius: 4,
        elevation: 2,
    },
    td: {
        border: '1px solid #000',
    },
    th: {
        border: "1px solid #000",
    }
}
